mapper.select:
select * from bbs_t order by b_idx desc 
LIMIT #{limit}  
OFFSET #{offset}

mapper.update:
update bbs_t 
set hit = hit +1
where b_idx = #{b_idx}

update bbs_t 
set status = 1
where b_idx = #{b_idx}

update bbs_t 
set subject =#{subject},
	content =#{content},
	writer =#{writer},
	f_name =#{f_name}
where b_idx =#{b_idx}
