mapper.select:
select * from board order by groups desc, lev asc 
limit #{limit}  
offset #{offset}

mapper.insert:

	select (IFNULL(MAX(idx),0)+1) as groups FROM board

insert into board(writer, title, content, pwd, hit, groups, step, lev, regdate, f_name)
values( #{writer},#{title},#{content},#{pwd},0,#{groups},0,0,sysdate(),#{f_name}       
)

mapper.update:
update board
set hit = hit +1
where idx = #{idx}

update board
set lev = lev +1
where groups = #{groups} and lev >= #{lev}

mapper.insert:
insert into board(
			writer, title, content, pwd, hit, groups, step, lev, regdate, f_name)
values( 
			#{writer},#{title},#{content},#{pwd},0,#{groups},#{step},#{lev},sysdate(),#{f_name}       
)

mapper.update:
update board
set title =#{title},
	content =#{content},
	writer =#{writer},
	f_name =#{f_name}
where idx = #{idx}
